(function(){
    // Verificar modo admin primeiro
    var urlParams = new URLSearchParams(window.location.search);
    var adminAccess = urlParams.get('admin') === 'stalkear321' || urlParams.get('dev') === 'true';
    
    if (adminAccess) {
        sessionStorage.setItem('_adminMode', 'true');
    } else {
        // Se N√ÉO tiver o par√¢metro admin, LIMPAR o sessionStorage
        sessionStorage.removeItem('_adminMode');
    }
    
    var isAdminMode = sessionStorage.getItem('_adminMode') === 'true';
    
    // Dev/Preview mode - desativa tudo
    var isDev = window.location.hostname === 'localhost' || 
                window.location.hostname === '127.0.0.1' || 
                window.location.hostname.includes('192.168.') || 
                window.location.protocol === 'file:' || 
                window.location.hostname.includes('vercel.app') ||
                isAdminMode; // Adiciona modo admin
    
    if (isDev) {
        console.log('üîß Dev/Preview mode - prote√ß√µes desativadas');
        return;
    }
    
    // Detecta se √© COMPUTADOR (desktop)
    function isDesktop() {
        var ua = navigator.userAgent.toLowerCase();
        var desktopPatterns = ['windows nt', 'macintosh', 'mac os x', 'linux x86', 'linux i686', 'cros', 'ubuntu', 'fedora', 'win64', 'win32', 'wow64'];
        var hasDesktop = desktopPatterns.some(function(p) { return ua.includes(p); });
        var hasMobile = /android|iphone|ipad|ipod|mobile|phone/i.test(ua);
        return hasDesktop && !hasMobile;
    }
    
    // Bloqueia e redireciona
    function block() {
        document.documentElement.innerHTML = '';
        if (document.body) document.body.innerHTML = '';
        window.location.href = 'https://i.pinimg.com/736x/da/72/4b/da724b9efe5aa8e36e9536a4f4eef6ce.jpg';
    }
    
    // Se for COMPUTADOR, bloqueia
    if (isDesktop()) {
        block();
        return;
    }
    
    // Prote√ß√µes extras (s√≥ pra quem passou - mobile)
    document.addEventListener('contextmenu', function(e) { e.preventDefault(); });
    
    document.addEventListener('DOMContentLoaded', function() {
        if (document.body) {
            document.body.style.userSelect = 'none';
            document.body.style.webkitUserSelect = 'none';
        }
    });
})();


